import React, { useContext } from 'react';
import { ShopContext } from '../Shop/Shop.js';
import { useNavigate } from 'react-router-dom';

export const Cart = () => {
  const { cartItems, data, totAmount, addToCart, removeFromCart } = useContext(ShopContext);
  const navigate = useNavigate();

  const handleRemoveFromCart = (id) => {
    removeFromCart(id);
  };

  const handleAddToCart = (id) => {
    addToCart(id);
  };

  return (
    <div className='container-sm'>
      <div>
        <h1>Your Cart Items</h1>
      </div>
      <div>
        {data.map((item) => {
          if (cartItems[item.id] !== 0) {
            return (
              <div key={item.id} className="d-flex align-items-center my-4 shadow p-3 mb-5 bg-body rounded">
                <div className="flex-shrink-0">
                  <img src={item.image} height="100px" width="100px" alt="..." />
                </div>
                <div className="flex-grow-1 ms-3">
                  <h5 className="card-title">{item.title}</h5>
                  <p className="card-text">${item.price}</p>
                  <div>
                    <button className="btn btn-outline-dark" onClick={() => handleRemoveFromCart(item.id)}>-</button>
                    <span> {cartItems[item.id]} </span>
                    <button className="btn btn-outline-dark" onClick={() => handleAddToCart(item.id)}>+</button>
                  </div>
                </div>
              </div>
            );
          }
          return null;
        })}
      </div>
      {totAmount() > 0 ? (
        <div className="bottom">
          <div className="subtotal">
            <h4>Subtotal Amount : $ {Math.round(totAmount() * 100) / 100} </h4>
          </div>
          <div className="my-5 pb-5">
            <button onClick={() => navigate("/")} className="btn btn-outline-info">Continue shopping</button>
            <button className="btn btn-outline-info mx-5">Checkout</button>
          </div>
        </div>
      ) : (
        <p>Your cart is empty</p>
      )}
    </div>
  );
};
