import React, { useState, useEffect } from "react";
import './App.css';
import Navbar from './components/navbar';
import SearchBox from './components/searchBox';
import Home from './pages/home';

// Agrega la función FetchMealByFirstLetter aquí
const FetchMealByFirstLetter = async (letter = '') => {
  const response = await fetch(`https://www.themealdb.com/api/json/v1/1/search.php?f=${letter}`);
  const data = await response.json();
  return data.meals;
}

function App() {
  const [meal, setMeal] = useState(null);
  const letter = 'b'; // Define la letra con la que deseas buscar comidas

  useEffect(() => {
    async function fetchRandomMeal() {
      try {
        const meals = await FetchMealByFirstLetter(letter);
        if (meals && meals.length > 0) {
          setMeal(meals[0]);
        } else {
          console.error('No se encontraron comidas con la letra especificada.');
        }
      } catch (error) {
        console.error('Error al obtener datos de la API: ', error);
      }
    }

    fetchRandomMeal();
  }, [letter]);

  return (
    <div className="App">
      <Navbar />
	  <SearchBox />
      <h1>Receta Aleatoria</h1>
      {meal && (
        <div>
          <h2>{meal.strMeal}</h2>
          <img src={meal.strMealThumb} alt={meal.strMeal} />
          <p>{meal.strInstructions}</p>
        </div>
      )}
      <Home />
    </div>
  );
}

export default App;
