import React, { useState, useEffect } from "react";
import "./App.css";
import Navbar from "./components/navbar";
import SearchBox from "./components/searchBox";
import Home from "./pages/home";
import { FetchMealByFirstLetter } from "./services/mealtService";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import ItemDetail from "./pages/details";

function App() {
  const [meals, setMeals] = useState([]);
  const letter = "b";

  useEffect(() => {
    async function fetchMeals() {
      try {
        const mealsData = await FetchMealByFirstLetter(letter);
        if (mealsData && mealsData.length > 0) {
          setMeals(mealsData);
        } else {
          console.error("No se encontraron comidas con la letra especificada.");
        }
      } catch (error) {
        console.error("Error al obtener datos de la API: ", error);
      }
    }
    fetchMeals();
  }, [letter]);

  return (
    <Router>
      <div className="App">
        <Navbar>
          <SearchBox />
        </Navbar>
        <Switch>
          <Route path="/" exact>
            <Home meals_list={meals} />
          </Route>
          <Route path="/item/:id">
            <ItemDetail />
          </Route>
        </Switch>
      </div>
    </Router>
  );
}

export default App;
