import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import NavBar from './NavBar';

// Importa la función FetchMealById desde mealService
import { FetchMealById } from './mealService';

function Detail() {
  const { id } = useParams();
  const [meal, setMeal] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Llama a la función FetchMealById con el ID de la comida
    FetchMealById(id)
      .then((data) => {
        if (data) {
          setMeal(data);
        } else {
          setError('No se encontraron detalles de la comida.');
        }
      })
      .catch((error) => {
        setError(error.message);
      });
  }, [id]);

  const handleAddToCart = () => {
    if (typeof window !== 'undefined' && window.localStorage) {
      const currentCart = JSON.parse(localStorage.getItem('cart')) || [];
      currentCart.push(meal);
      localStorage.setItem('cart', JSON.stringify(currentCart));
    }
  };

  if (!meal) {
    return <div>Cargando...</div>;
  }

  return (
    <div className='container'>
      <div className='navigationbar-container'>
        <NavBar />
      </div>
      <div className='all-container'>
        {error ? (
          <div className='error-message'>{error}</div>
        ) : (
          <div className='detail-container'>
            <div key={meal.idMeal} className='card-detail'>
              <div className='image-detail'>
                <div className='product-image-detail'>
                  {meal.strMealThumb ? (
                    <img src={meal.strMealThumb} alt={meal.strMeal} />
                  ) : (
                    <p>No hay imagen disponible</p>
                  )}
                </div>
              </div>
              <div className='detail'>
                <h2>{meal.strMeal}</h2>
                <p>Categoría: {meal.strCategory}</p>
                <p>Área: {meal.strArea}</p>
                <p>Ingredientes: {meal.strIngredient1}, {meal.strIngredient2}, ...</p>
                <p>Instrucciones: {meal.strInstructions}</p>
              </div>
            </div>
            <button onClick={handleAddToCart} className='cart-button'>
              Añadir al carrito
            </button>
          </div>
        )}
      </div>
    </div>
  );
}

export default Detail;
