import React, { useState, useEffect } from "react";
import "./App.scss";

function ShopItems() {
  const [items, setItems] = useState([]);
  const [visibleItems, setVisibleItems] = useState([]);
  const [showMore, setShowMore] = useState(false);

  useEffect(() => {
    fetch("https://fakestoreapi.com/products")
      .then((response) => response.json())
      .then((data) => {
        setItems(data);
        setVisibleItems(data.slice(0, 6)); // Mostrar los primeros 6 elementos
      })
      .catch((error) => console.error(error));
  }, []);

  const handleShowMoreClick = () => {
    setVisibleItems(items); // Mostrar todos los elementos restantes
    setShowMore(true); // Ocultar el botón "Ver más" después de hacer clic
  };

  return (
    <div>
      <div className="title">
        <h1>Shop Items</h1>
      </div>

      <div className="container">
        {items.map((item) => (
          <div key={item.id} className="product_card">
            <div className="item_image">
              <img src={item.image} alt="#" />
            </div>

            <div className="card_description">
              <div className="item_name">
                <span>{item.title}</span>
              </div>
              <div className="item_price">
                <span>Price: $ {item.price}</span>
              </div>
              <div className="item_category">
                <span>Category: {item.category}</span>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default ShopItems;
