import React, { useState, useEffect } from "react";
import './App.css';
import Navbar from './components/navbar';
import SearchBox from './components/searchBox';
import Home from './pages/home';

// Agrega la función FetchMealsByFirstLetter aquí
const FetchMealsByFirstLetter = async (letter = '') => {
  const response = await fetch(`https://www.themealdb.com/api/json/v1/1/search.php?f=${letter}`);
  const data = await response.json();
  return data.meals;
}

function App() {
  const [meals, setMeals] = useState([]);
  const letter = 'b'; // Define la letra con la que deseas buscar comidas

  useEffect(() => {
    async function fetchMeals() {
      try {
        const mealsData = await FetchMealsByFirstLetter(letter);
        if (mealsData) {
          setMeals(mealsData);
        } else {
          console.error('No se encontraron comidas con la letra especificada.');
        }
      } catch (error) {
        console.error('Error al obtener datos de la API: ', error);
      }
    }

    fetchMeals();
  }, [letter]);

  return (
    <div className="App">
      <Navbar />
      <h1>Comidas que comienzan con la letra "{letter.toUpperCase()}"</h1>
      <ul>
        {meals.map((meal) => (
          <li key={meal.idMeal}>{meal.strMeal}</li>
        ))}
      </ul>
      <SearchBox />
      <Home />
    </div>
  );
}

export default App;
