import React, { useState, useEffect } from 'react';
import Shop from './Components/Shop.js';
import Detail from './Components/ItemDetail';
import Cart from './Components/Cart';
import Filter from './Components/Filter';
import { BrowserRouter, Route, Routes } from 'react-router-dom';

function App() {
  const [products, setProducts] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState("all");
  const [cart, setCart] = useState([]); // Estado para el carrito de compras
  const categories = ["all", "women's clothing", "electronics", "jewelry", "men's clothing"];

  useEffect(() => {
    fetch("https://fakestoreapi.com/products")
      .then((response) => response.json())
      .then((data) => setProducts(data))
      .catch((error) => console.error("Error al obtener los productos", error));
  }, []);

  // Función para agregar un producto al carrito
  const addToCart = (product) => {
    setCart([...cart, product]);
  };

  return (
    <BrowserRouter>
      <div className="app">
        <Filter
          categories={categories}
          selectedCategory={selectedCategory}
          setSelectedCategory={setSelectedCategory}
        />
        <Routes>
          <Route
            path="/"
            element={<Shop products={products} selectedCategory={selectedCategory} />}
          />
          <Route path="/products/:id" element={<Detail addToCart={addToCart} />} />
          <Route path="/cart" element={<Cart cart={cart} />} />
        </Routes>
      </div>
    </BrowserRouter>
  );
}

export default App;
