import React, { useState, useEffect } from "react";
import './App.css';
import Navbar from './components/navbar';
import SearchBox from './components/searchBox';
import Home from './pages/home';
import { FetchMealByFirstLetter } from './mealtService';
function App() {
  const [meals, setMeals] = useState([]);
  const letter = 'b'; // Define la letra con la que deseas buscar comidas

  useEffect(() => {
    async function fetchMeals() {
      try {
        const mealsData = await FetchMealByFirstLetter(letter);
        if (mealsData && mealsData.length > 0) {
          setMeals(mealsData);
        } else {
          console.error('No se encontraron comidas con la letra especificada.');
        }
      } catch (error) {
        console.error('Error al obtener datos de la API: ', error);
      }
    }

    fetchMeals();
  }, [letter]);

  return (
    <div className="App">
      <Navbar />
      <h1>Comidas que comienzan con la letra "{letter.toUpperCase()}"</h1>
      <div className="meals-container">
        {meals.map((meal) => (
          <div key={meal.idMeal} className="meal-item">
            <img src={meal.strMealThumb} alt={meal.strMeal} />
            <p>{meal.strMeal}</p>
          </div>
        ))}
      </div>
      <SearchBox />
      <Home />
    </div>
  );
}

export default App;
