import React, { useState, useEffect } from "react";
import "./Shop.scss";
import NavigationBar from './NavigationBar';
import { Link } from 'react-router-dom';

function ShopItems() {
  const [items, setItems] = useState([]);
  const [user, setUser] = useState({});
  const [visibleItems, setVisibleItems] = useState([]);
  const [showMore, setShowMore] = useState(false);
  const initialItemCount = 6; 

  useEffect(() => {
    fetch('https://fakestoreapi.com/users/6')
            .then(res=>res.json())
            .then((data) => {
              setUser(data);
            })
    fetch("https://fakestoreapi.com/products")
      .then((response) => response.json())
      .then((data) => {
        setItems(data);
        setVisibleItems(data.slice(0, initialItemCount)); 
      })
      .catch((error) => console.error(error));
  }, []);

  const handleShowMoreClick = () => {
    setVisibleItems(items); 
    setShowMore(true); 
  };

  return (
    <div>
      <NavigationBar />
      <div className="title">
        <h1>Shop Items</h1>
        {user.name && (
          <div className="user">
            <span>{user.name.firstname[0].toUpperCase()}</span>
            <span>{user.name.lastname[0].toUpperCase()}</span>
          </div>
        )}
      </div>
      <div className="container">
        {visibleItems.map((item) => (
          <div key={item.id} className="product_card">
            <div className="item_image">
              <img src={item.image} alt="#" />
            </div>
            <div className="card_description">
              <div className="item_name">
                <span>{item.title}</span>
              </div>
              <div className="item_price">
                <span>Price: $ {item.price}</span>
              </div>
              <div className="item_category">
                <span>Category: {item.category}</span>
              </div>
              <Link to={`/products/${item.id}`}>Ver Detalles</Link>
            </div>
          </div>
        ))}  
      </div>
      <div className="button">
          {!showMore && (
            <button onClick={handleShowMoreClick} className="show_more_button">
              Ver m√°s
            </button>
          )}
        </div>
    </div>
  );
}

export default ShopItems;
