import React, { useState, useEffect } from "react";
import "./Shop.scss";
import { Link } from "react-router-dom";

function ShopItems() {
  const [items, setItems] = useState([]);
  const [user, setUser] = useState({});
  const [visibleItems, setVisibleItems] = useState([]);
  const [showMore, setShowMore] = useState(false);
  const [selectedCategory, setSelectedCategory] = useState("all"); // Nuevo estado para la categoría seleccionada
  const initialItemCount = 6;

  useEffect(() => {
    fetch("https://fakestoreapi.com/users/6")
      .then((res) => res.json())
      .then((data) => {
        setUser(data);
      });
    fetch("https://fakestoreapi.com/products")
      .then((response) => response.json())
      .then((data) => {
        setItems(data);
        setVisibleItems(data.slice(0, initialItemCount));
      })
      .catch((error) => console.error(error));
  }, []);

  useEffect(() => {
    // Filtrar los elementos visibles según la categoría seleccionada
    const filteredItems = selectedCategory === "all"
      ? items
      : items.filter((item) => item.category === selectedCategory);

    setVisibleItems(filteredItems.slice(0, initialItemCount)); // Mostrar los primeros elementos
    setShowMore(false); // Restablecer el estado "showMore" al cambiar la categoría
  }, [selectedCategory, items]);

  const handleShowMoreClick = () => {
    setVisibleItems(items); // Mostrar todos los elementos
    setShowMore(true);
  };

  const handleCategoryChange = (e) => {
    setSelectedCategory(e.target.value); // Cambiar la categoría seleccionada
  };

  return (
    <div>
      <div className="title">
        <h1>Shop Items</h1>
        {user.name && (
          <div className="user">
            <span>{user.name.firstname[0].toUpperCase()}</span>
            <span>{user.name.lastname[0].toUpperCase()}</span>
          </div>
        )}
      </div>
      <div className="category-filter">
        <label htmlFor="category">Filtrar por categoría:</label>
        <select id="category" value={selectedCategory} onChange={handleCategoryChange}>
          <option value="all">Todas</option>
          <option value="electronics">Electrónica</option>
          <option value="jewelery">Joyería</option>
          <option value="men's clothing">Ropa para Hombres</option>
          <option value="women's clothing">Ropa para Mujeres</option>
        </select>
      </div>
      <div className="container">
        {visibleItems.map((item) => (
          <div key={item.id} className="product_card">
            <div className="item_image">
              <img src={item.image} alt="#" />
            </div>
            <div className="card_description">
              <div className="item_name">
                <span>{item.title}</span>
              </div>
              <div className="item_price">
                <span>Precio: $ {item.price}</span>
              </div>
              <div className="item_category">
                <span>Categoría: {item.category}</span>
              </div>
              <Link to={`/products/${item.id}`} className="link-to-details">
                Ver Detalles
              </Link>
            </div>
          </div>
        ))}
      </div>
      <div className="button">
        {!showMore && (
          <button onClick={handleShowMoreClick} className="show_more_button">
            Ver más
          </button>
        )}
      </div>
    </div>
  );
}

export default ShopItems;
